package assignments;

public class Email {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int email=0;
	     for(int day=1;day<=10;day++)
	     {
	         System.out.println("Day"+day+":");
	         email++;
	         System.out.println("Inbox has"+email+"emails");
	     }

	}

}